import { useEffect, useState } from "react";
import { FakeStoreApi } from '../../services/fake-store-api';
import { useSearchParams } from "react-router-dom";
import { Item } from "../../components/item";
import { useCart } from "../../context/cart";

const Products = () => {
    const [loading, setLoading] = useState(true);
    const [products, setProducts] = useState([]);
    const [query] = useSearchParams();
    const { addToCart } = useCart();

    const [sortBy, setSortBy] = useState(''); // 排序字段
    const [sortOrder, setSortOrder] = useState('asc'); // 排序顺序

    const searchQuery = query.get('q');

    useEffect(() => {
        const fetchProducts = async () => {
            setLoading(true);
            const products = searchQuery ? 
                await FakeStoreApi.fetchProductsBySearchQuery(searchQuery) : 
                await FakeStoreApi.fetchAllProducts();
            setProducts(products);
            setLoading(false);
        };
        fetchProducts().catch(console.error);
    }, [searchQuery]);

    const handleSort = (field) => {
        // 切换排序方式和顺序
        if (sortBy === field) {
            setSortOrder(prev => (prev === 'asc' ? 'desc' : 'asc'));
        } else {
            setSortBy(field);
            setSortOrder('asc');
        }
    };

    const sortedProducts = () => {
        if (sortBy) {
            return [...products].sort((a, b) => {
                if (sortOrder === 'asc') {
                    return a[sortBy] > b[sortBy] ? 1 : -1;
                } else {
                    return a[sortBy] < b[sortBy] ? 1 : -1;
                }
            });
        }
        return products; // 返回未排序的产品
    };

    if (!loading && searchQuery && !products.length) {
        return (
            <div className="container">
                <div className="product py-2">
                    <div className="details p-3">没有找到匹配您查询的产品。</div>
                </div>
            </div>
        );
    }

    return (
        <>
            <div className="container">
                <div className="sortButtons">
                    <button onClick={() => handleSort('price')}>按价格</button>
                    <button onClick={() => handleSort('rating')}>按评价</button>
                </div>
                <div className="products my-5">
                    <div className="grid">
                        {loading ? (
                            <div className="loader" />
                        ) : (
                            sortedProducts().map((product) => (
                                <Item key={product.id} data={product} addToCart={() => addToCart(product)} />
                            ))
                        )}
                    </div>
                </div>
            </div>
        </>
    );
};

export { Products };
