import { useEffect, useState } from "react";
import { FakeStoreApi } from '../../services/fake-store-api';
import { useSearchParams } from "react-router-dom";
import { Item } from "../../components/item";
import { useCart } from "../../context/cart";

const Products = () => {
    const [loading, setLoading] = useState(true);
    const [products, setProducts] = useState([]);
    const [query] = useSearchParams();
    const { addToCart } = useCart();

    const [sortOrder, setSortOrder] = useState({ key: 'price', order: 'asc' }); // 新增状态

    const searchQuery = query.get('q');

    useEffect(() => {
        const fetchProducts = async () => {
            setLoading(true);
            const products = searchQuery ? await FakeStoreApi.fetchProductsBySearchQuery(searchQuery) : await FakeStoreApi.fetchAllProducts();
            setProducts(products);
            setLoading(false);
        }
        fetchProducts().catch(console.error);
    }, [searchQuery]);

    // 排序函数
    const sortProducts = (key) => {
        const order = sortOrder.key === key && sortOrder.order === 'asc' ? 'desc' : 'asc'; // 切换排序方式
        setSortOrder({ key, order }); // 更新排序状态

        const sortedProducts = [...products].sort((a, b) => {
            if (order === 'asc') {
                return a[key] < b[key] ? -1 : 1; // 升序
            } else {
                return a[key] > b[key] ? -1 : 1; // 降序
            }
        });

        setProducts(sortedProducts); // 更新排序后的产品列表
    };

    // 根据当前的排序方式对产品进行排序
    useEffect(() => {
        if (sortOrder.key) {
            const sortedProducts = [...products].sort((a, b) => {
                if (sortOrder.order === 'asc') {
                    return a[sortOrder.key] < b[sortOrder.key] ? -1 : 1;
                } else {
                    return a[sortOrder.key] > b[sortOrder.key] ? -1 : 1;
                }
            });
            setProducts(sortedProducts);
        }
    }, [sortOrder]); // 当排序状态变化时触发排序

    if (!loading && searchQuery && !products.length) {
        return (
            <div className="container">
                <div className="product py-2">
                    <div className="details p-3">No products found matching your query.</div>
                </div>
            </div>
        )
    }

    return (
        <div className="container">
            <div className="sortButtons">
                <button onClick={() => sortProducts('price')}>按价格</button>
                <button onClick={() => sortProducts('rating')}>按评价</button>
            </div>
            <div className="products my-5">
                <div className="grid">
                    {loading ? (
                        <div className="loader" />
                    ) : (
                        products.map((product) => (
                            <Item key={product.id} data={product} addToCart={() => addToCart(product)} />
                        ))
                    )}
                </div>
            </div>
        </div>
    )
}

export { Products };
