import './product.css' // 导入产品页面的样式文件
import { useEffect, useState } from "react" // 从 React 导入 useEffect 和 useState 钩子，用于管理组件状态和副作用
import { FakeStoreApi } from '../../services/fake-store-api' // 从服务文件中导入 FakeStoreApi，用于获取产品数据
import { Link, useParams } from "react-router-dom" // 导入 Link 和 useParams，用于路由处理
import { useCart } from "../../context/cart" // 从购物车上下文中导入 useCart，用于添加商品到购物车

// Product 组件用于显示特定产品的详细信息
const Product = () => {
    const [loading, setLoading] = useState(true); // 定义 loading 状态，表示产品数据是否正在加载
    const [product, setProduct] = useState(); // 定义 product 状态，用于存储获取到的产品数据
    const { productId } = useParams(); // 从 URL 参数中获取 productId
    const { addToCart } = useCart(); // 从购物车上下文中解构出 addToCart 方法，用于将产品添加到购物车

    // 使用 useEffect 钩子在组件加载时获取产品数据
    useEffect(() => {
        // 定义异步函数 fetchProduct 来获取产品数据
        const fetchProduct = async () => {
            setLoading(true); // 设置加载状态为 true
            const product = await FakeStoreApi.fetchProductById(productId); // 通过 productId 从 API 获取产品数据
            setProduct(product); // 将获取到的产品数据存储到 product 状态中
            setLoading(false); // 加载完成后将 loading 状态设置为 false
        }
        // 调用 fetchProduct 并处理错误
        fetchProduct().catch(console.error)
    }, [productId]) // 当 productId 变化时重新执行 useEffect

    // 如果产品加载完毕但未找到产品，显示“未找到产品”的消息
    if (!loading && !product) {
        return (
            <div className="container">
                <div className="product py-2">
                    <div className="details p-3">
                        {/* 如果未找到产品，显示错误提示并提供返回首页的链接 */}
                        Product not found. Please visit{" "}
                        <Link to="/" replace>
                            home
                        </Link>{" "}
                        to see all available products
                    </div>
                </div>
            </div>
        )
    }

    // 返回产品详细信息页面的 JSX 结构
    return (
        <div className="container">
            {loading ? (
                // 如果产品正在加载，显示加载指示器
                <div className={"loader"}></div>
            ) : (
                // 如果产品加载完成，显示产品的详细信息
                <div className="product py-2">
                    <div className="details grid p-3"> {/* 产品详情的网格布局 */}
                        <div className="product-image"> {/* 产品图片区域 */}
                            <img src={product.image} alt="" /> {/* 显示产品图片 */}
                        </div>
                        <div className="info"> {/* 产品信息区域 */}
                            <div className="description"> {/* 产品描述 */}
                                <h3>{product.title}</h3> {/* 显示产品标题 */}
                                <p className=" my-2">{product.description}</p> {/* 显示产品描述 */}
                            </div>
                            <div className="flex"> {/* 产品价格和购物车按钮 */}
                                <span className="price">${product.price}</span> {/* 显示产品价格 */}
                                <span className="cart" onClick={() => addToCart(product)}> {/* 点击添加到购物车 */}
                                    <img src="/cart.svg" alt="" /> {/* 购物车图标 */}
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    )
}

export { Product } // 导出 Product 组件
